package com.lao.Map;

import java.util.LinkedHashMap;

public class linkedHashmapExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		LinkedHashMap <String,String> hero = new LinkedHashMap <String,String>();
		hero.put("Iorn Man", "Tonystark");
		hero.put("Batman", "Bruce wayne");
		hero.put("Superman", "Calrk");
		
		System.out.println(hero);
		
		LinkedHashMap <String,String> Hasmapheros = new LinkedHashMap <String,String>();
		Hasmapheros.put("Iorn Man", "Tonystark");
		Hasmapheros.put("Batman", "Bruce wayne");
		Hasmapheros.put("Superman", "Calrk");
		
		
		System.out.println(Hasmapheros);
		
	}

}
